}

interface ModuleInteractionAnalysis {
  coreAnalysisModules: {
    structure: MizanModuleConfig;
    culture: MizanModuleConfig;
    skills: MizanModuleConfig;
  };
  businessModules: {
    performance: MizanModuleConfig;
    hiring: MizanModuleConfig;
  };
