}

/**
 * Extracts and analyzes Mizan platform module interactions
 * Ensures compliance with Three-Engine Architecture and agent triggering patterns
 * Maintains tenant isolation and business workflow integrity
 */
function extractModuleInteractions(agentContext: AgentContext): ModuleInteractionAnalysis {
  try {
    // Validate tenant isolation in context
    if (!agentContext.tenantId || agentContext.tenantId.trim() === '') {
      throw new Error('AgentContext must include valid tenantId for Mizan compliance');
    }

    // Extract core analysis modules with Three-Engine compliance
    const coreAnalysisModules = {
      structure: extractModuleConfig(agentContext, 'structure', 'core_analysis'),
      culture: extractModuleConfig(agentContext, 'culture', 'core_analysis'),
      skills: extractModuleConfig(agentContext, 'skills', 'core_analysis')
    };

    // Extract business modules (complete systems)
    const businessModules = {
      performance: extractModuleConfig(agentContext, 'performance', 'business_module'),
      hiring: extractModuleConfig(agentContext, 'hiring', 'business_module')
    };

    // Extract triggered modules (activated by analysis results)
    const triggeredModules = {
      lxp: extractModuleConfig(agentContext, 'lxp', 'triggered_module'),
      talent: extractModuleConfig(agentContext, 'talent', 'triggered_module'),
      bonus: extractModuleConfig(agentContext, 'bonus', 'triggered_module')
    };

    // Extract agent triggering configurations
    const agentTriggers = {
      recognition: extractAgentTriggerConfig(agentContext, 'recognition', 'culture'),
      engagement: extractAgentTriggerConfig(agentContext, 'engagement', 'culture')
    };

    // Validate workflow integrity
    const workflowIntegrity = validateWorkflowIntegrity(agentContext.workflows);

    // Validate tenant isolation compliance
    const tenantIsolationCompliance = validateTenantIsolation(agentContext);

    // Validate Three-Engine Architecture compliance
    const threeEngineCompliance = validateThreeEngineCompliance(agentContext.architecturePatterns);

    return {
      coreAnalysisModules,
      businessModules,
      triggeredModules,
      agentTriggers,
      workflowIntegrity,
      tenantIsolationCompliance,
      threeEngineCompliance
    };

  } catch (error) {
    console.error('Error extracting Mizan module interactions:', error);
    throw new Error(`Failed to extract module interactions: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Extracts module configuration with Mizan compliance validation
 */
function extractModuleConfig(
  agentContext: AgentContext, 
  moduleName: string, 
  moduleType: MizanModuleConfig['type']
): MizanModuleConfig {
  const moduleConfig = agentContext.modules.find(m => m.name === moduleName);

  if (!moduleConfig) {
    throw new Error(`Required Mizan module '${moduleName}' not found in agent context`);
  }

  // Validate Mizan compliance requirements
  if (!moduleConfig.tenantIsolated) {
    throw new Error(`Module '${moduleName}' must be tenant isolated for Mizan compliance`);
  }

  if (moduleType === 'core_analysis' && !moduleConfig.threeEngineCompliant) {
    throw new Error(`Core analysis module '${moduleName}' must be Three-Engine compliant`);
  }

  return {
    ...moduleConfig,
    type: moduleType
  };
}

/**
 * Extracts agent trigger configuration with validation
 */
function extractAgentTriggerConfig(
  agentContext: AgentContext,
  agentName: string,
  triggerModule: string
): AgentTriggerConfig {
  const workflow = agentContext.workflows.find(w => 
    w.name.toLowerCase().includes(agentName) && 
    w.sourceModule === triggerModule
  );

  if (!workflow) {
    throw new Error(`Agent trigger workflow for '${agentName}' not found`);
  }

  if (!workflow.tenantIsolated) {
    throw new Error(`Agent trigger for '${agentName}' must be tenant isolated`);
  }

  return {
    triggeredBy: workflow.sourceModule,
    triggerCondition: workflow.triggerCondition,
    dataRequired: workflow.dataFlow.map(df => df.field),
    outputFormat: 'mizan_agent_analysis',
    tenantIsolated: workflow.tenantIsolated
  };
}

/**
 * Validates workflow integrity across Mizan modules
 */
function validateWorkflowIntegrity(workflows: WorkflowDefinition[]): boolean {
  const requiredWorkflows = [
    'culture_to_recognition',
    'culture_to_engagement', 
    'skills_to_lxp',
    'performance_to_talent',
    'performance_to_bonus',
    'structure_to_hiring'
  ];

  const existingWorkflows = workflows.map(w => w.id.toLowerCase());

  return requiredWorkflows.every(required => 
    existingWorkflows.some(existing => existing.includes(required))
  );
}

/**
 * Validates tenant isolation compliance
 */
function validateTenantIsolation(agentContext: AgentContext): boolean {
  // Check all modules are tenant isolated
  const moduleCompliance = agentContext.modules.every(m => m.tenantIsolated);

  // Check all workflows are tenant isolated
  const workflowCompliance = agentContext.workflows.every(w => w.tenantIsolated);

  // Check all integration points require tenant isolation
  const integrationCompliance = agentContext.integrationPoints.every(ip => ip.tenantIsolationRequired);

  return moduleCompliance && workflowCompliance && integrationCompliance;
}

/**
 * Validates Three-Engine Architecture compliance
 */
function validateThreeEngineCompliance(patterns: ArchitecturePattern[]): boolean {
  const threeEnginePattern = patterns.find(p => p.pattern === 'three_engine');
  return threeEnginePattern?.compliance ?? false;
}

