async function generateCodeWithClaude(
  options: ClaudeGenerationOptions
): Promise<MizanCodeGenerationResult> {
  const {
    prompt,
    systemMessage = 'You are an expert developer generating production-ready code for the Mizan Platform following Three-Engine Architecture, multi-tenant isolation, and complete business workflow patterns.',
    attempt = 1,
    maxRetries = 3,
    temperature = 0.1
  } = options;

  if (!prompt || typeof prompt !== 'string' || prompt.trim().length === 0) {
    throw new Error('MIZAN_ERROR: Prompt is required and must be a non-empty string');
  }

  if (attempt > maxRetries) {
    throw new Error(`MIZAN_ERROR: Maximum retry attempts (${maxRetries}) exceeded for code generation`);
  }

  try {
    // Enhanced system message with Mizan-specific requirements
    const mizanSystemMessage = `${systemMessage}

CRITICAL MIZAN PLATFORM REQUIREMENTS:
1. Use TypeScript strict mode with explicit types (NO 'any' types)
2. Implement multi-tenant isolation with tenantId in ALL database queries
3. Use Drizzle ORM for database operations (NOT Prisma)
4. Use Next.js 14 App Router patterns (NOT Pages Router)
5. Follow Three-Engine Architecture: KnowledgeEngine  DataEngine  ReasoningEngine
6. Implement agent triggering: Culture  Recognition/Engagement, Skills  LXP
7. Maintain business workflow completeness and cross-module integration
8. Include comprehensive error handling with try-catch blocks
9. NO mock data, NO placeholders, NO TODO comments
10. Production-ready code ONLY

Generate code that maintains Mizan's strategic HR analytics workflows and architectural integrity.`;

    const claudeResponse: ClaudeResponse = await callClaudeAPI({
      prompt,
      systemMessage: mizanSystemMessage,
      temperature,
      maxTokens: 4000
    });

    // Validate Mizan compliance
    const mizanCompliance = validateMizanCompliance(claudeResponse.content);
    const businessLogic = validateBusinessLogic(claudeResponse.content);

    const result: MizanCodeGenerationResult = {
      code: claudeResponse.content,
      explanation: extractExplanation(claudeResponse.content),
      mizanCompliance,
      businessLogic,
      confidence: calculateConfidence(mizanCompliance, businessLogic, attempt),
      attempt,
      timestamp: new Date()
    };

    // Log generation for audit trail
    logMizanCodeGeneration(result);

    return result;

  } catch (error) {
    console.error(`MIZAN_ERROR: Code generation failed on attempt ${attempt}:`, error);

    if (attempt < maxRetries) {
      console.log(`MIZAN_RETRY: Retrying code generation (attempt ${attempt + 1}/${maxRetries})`);
      return generateCodeWithClaude({
        ...options,
        attempt: attempt + 1
      });
    }

    throw new Error(`MIZAN_FATAL: Code generation failed after ${maxRetries} attempts: ${error.message}`);
  }
}

function validateMizanCompliance(code: string): MizanCodeGenerationResult['mizanCompliance'] {
  return {
    threeEnginePattern: /KnowledgeEngine|DataEngine|ReasoningEngine/.test(code),
    tenantIsolation: /tenantId/.test(code) && /eq\(.*\.tenantId/.test(code),
    typeScriptStrict: /interface|type|:\s*\w+/.test(code) && !/:\s*any/.test(code),
    drizzleORM: /drizzle-orm|db\.select|db\.insert|db\.update/.test(code) && !/prisma/.test(code),
    nextJsAppRouter: /app\/.*\/page\.tsx|NextResponse|params:/.test(code) && !/getServerSideProps|getStaticProps/.test(code)
  };
}

function validateBusinessLogic(code: string): MizanCodeGenerationResult['businessLogic'] {
  return {
    moduleIntegration: /Culture.*Recognition|Skills.*LXP|Performance.*Culture/.test(code),
    agentTriggering: /trigger.*Agent|Agent.*trigger/.test(code),
    workflowCompleteness: /workflow|process|complete/.test(code) && !/TODO|mock|placeholder/.test(code)
  };
}

function calculateConfidence(
  mizanCompliance: MizanCodeGenerationResult['mizanCompliance'],
  businessLogic: MizanCodeGenerationResult['businessLogic'],
  attempt: number
): number {
  const complianceScore = Object.values(mizanCompliance).filter(Boolean).length / Object.keys(mizanCompliance).length;
  const businessScore = Object.values(businessLogic).filter(Boolean).length / Object.keys(businessLogic).length;
  const attemptPenalty = (attempt - 1) * 0.1;

  return Math.max(0, Math.min(1, (complianceScore + businessScore) / 2 - attemptPenalty));
}

function extractExplanation(code: string): string {
  const explanationMatch = code.match(/\/\*\*\s*EXPLANATION:(.*?)\*\//s);
  return explanationMatch ? explanationMatch[1].trim() : 'No explanation provided';
}

function logMizanCodeGeneration(result: MizanCodeGenerationResult): void {
  console.log('MIZAN_CODE_GENERATION:', {
    timestamp: result.timestamp.toISOString(),
    attempt: result.attempt,
    confidence: result.confidence,
    mizanCompliance: result.mizanCompliance,
    businessLogic: result.businessLogic
  });
}

