/**
 * Extracts code blocks from AI agent responses with strict typing
 * Part of Mizan's Three-Engine Architecture validation system
 * @param {AgentResponse} response - Structured response from AI agent
 * @returns {ExtractedCode} Parsed code blocks with metadata
 */
function extractCodeFromResponse(response: AgentResponse): ExtractedCode {
  // Validate response structure for Mizan agent system
  if (!response || typeof response !== 'object') {
    throw new MizanAgentError('Invalid agent response structure', 'INVALID_RESPONSE');
  }

  // Handle different response formats from Mizan's multi-agent system
  const responseText: string = response.content || response.text || response.message || '';

  if (!responseText || typeof responseText !== 'string') {
    throw new MizanAgentError('Missing or invalid response content', 'MISSING_CONTENT');
  }

  // Extract code blocks using regex patterns for different formats
  const codeBlocks: CodeBlock[] = [];

  // Pattern 1: Standard markdown code blocks
  const markdownPattern = /```(\w+)?\n?([\s\S]*?)```/g;
  let match: RegExpExecArray | null;

  while ((match = markdownPattern.exec(responseText)) !== null) {
    codeBlocks.push({
      language: match[1] || 'unknown',
      content: match[2].trim(),
      startIndex: match.index,
      endIndex: match.index + match[0].length,
      type: 'markdown'
    });
  }

  // Pattern 2: XML-style code blocks (for Mizan agent responses)
  const xmlPattern = /<code(?:\s+lang="(\w+)")?>([\s\S]*?)<\/code>/g;

  while ((match = xmlPattern.exec(responseText)) !== null) {
    codeBlocks.push({
      language: match[1] || 'unknown',
      content: match[2].trim(),
      startIndex: match.index,
      endIndex: match.index + match[0].length,
      type: 'xml'
    });
  }

  // Pattern 3: Inline code for small snippets
  const inlinePattern = /`([^`\n]+)`/g;

  while ((match = inlinePattern.exec(responseText)) !== null) {
    if (match[1].length > 10) { // Only capture meaningful code snippets
      codeBlocks.push({
        language: 'inline',
        content: match[1].trim(),
        startIndex: match.index,
        endIndex: match.index + match[0].length,
        type: 'inline'
      });
    }
  }

  // Sort by position in text
  codeBlocks.sort((a, b) => a.startIndex - b.startIndex);

  // Validate extracted code for Mizan compliance
  const validatedBlocks = codeBlocks.map(block => ({
    ...block,
    isMizanCompliant: validateMizanCodeCompliance(block),
    hasTypeScript: block.language === 'typescript' || block.language === 'ts',
    hasTenantIsolation: block.content.includes('tenantId'),
    usesDrizzleORM: block.content.includes('drizzle') || block.content.includes('db.select'),
    usesAppRouter: block.content.includes('app/') && !block.content.includes('pages/')
  }));

  return {
    totalBlocks: validatedBlocks.length,
    codeBlocks: validatedBlocks,
    hasValidCode: validatedBlocks.length > 0,
    mizanCompliantBlocks: validatedBlocks.filter(block => block.isMizanCompliant).length,
    extractedAt: new Date().toISOString(),
    agentId: response.agentId || 'unknown',
    responseId: response.id || generateResponseId()
  };
}

/**
 * Validates code block compliance with Mizan architecture standards
 * @param {CodeBlock} block - Code block to validate
 * @returns {boolean} Whether the code meets Mizan standards
 */
function validateMizanCodeCompliance(block: CodeBlock): boolean {
  const content = block.content.toLowerCase();

  // Check for forbidden patterns
  const forbiddenPatterns = [
    'any', // No 'any' types
    'todo:', // No TODO comments
    'mock', // No mock data
    'placeholder', // No placeholders
    'prisma', // No Prisma ORM
    'pages/', // No Pages Router
    'getserversideprops', // No old Next.js patterns
    'getstatic' // No static generation patterns
  ];

  const hasForbiddenPatterns = forbiddenPatterns.some(pattern => 
    content.includes(pattern)
  );

  if (hasForbiddenPatterns) return false;

  // Check for required patterns in TypeScript files
  if (block.language === 'typescript' || block.language === 'ts') {
    const requiredPatterns = [
      'interface', // Must have proper interfaces
      ': string', // Must have type annotations
      'async', // Must handle async operations
      'try', // Must have error handling
      'catch' // Must have error handling
    ];

    const hasRequiredPatterns = requiredPatterns.some(pattern => 
      content.includes(pattern)
    );

    return hasRequiredPatterns;
  }

  return true; // Non-TypeScript files pass basic validation
}

/**
