  THREE_ENGINE_COMPLIANCE: true
};

/**
 * Generates Mizan-compliant code following Three-Engine Architecture
 * Ensures tenant isolation, business workflow preservation, and strategic alignment
 */
async function generateCompliantCode(
  basePrompt: string,
  fileName: string,
  agentContext: MizanAgentContext,
  maxAttempts: number = CONFIG.MAX_REFINEMENT_ATTEMPTS
): Promise<CodeGenerationResult> {
  try {
    // Validate agent context for Mizan compliance
    const validatedContext = MizanAgentContextSchema.parse(agentContext);

    // Ensure tenant isolation is maintained
    if (!validatedContext.tenantId) {
      throw new Error('Tenant isolation required: tenantId must be provided');
    }

    // Build Mizan-specific prompt with architectural requirements
    const mizanPrompt = buildMizanCompliantPrompt(basePrompt, fileName, validatedContext);

    let attempt = 0;
    let lastError: string | null = null;

    while (attempt < maxAttempts) {
      try {
        const generatedCode = await generateCodeWithMizanPatterns(mizanPrompt, validatedContext);

        // Validate generated code for Mizan compliance
        const validation = await validateMizanCompliance(generatedCode, validatedContext);

        if (validation.success) {
          return {
            success: true,
            code: generatedCode,
            mizanCompliance: validation.mizanCompliance,
            businessLogic: validation.businessLogic
          };
        }

        lastError = validation.errors?.join(', ') || 'Unknown validation error';
        attempt++;

      } catch (error) {
        lastError = error instanceof Error ? error.message : 'Unknown error';
        attempt++;
      }
    }

    return {
      success: false,
      code: '',
      mizanCompliance: {
        threeEnginePattern: false,
        tenantIsolation: false,
        typeScriptStrict: false,
        productionReady: false
      },
      businessLogic: {
        workflowsPreserved: [],
        moduleIntegration: false,
        agentTriggering: false
      },
      errors: [`Failed after ${maxAttempts} attempts. Last error: ${lastError}`]
    };

  } catch (error) {
    return {
      success: false,
      code: '',
      mizanCompliance: {
        threeEnginePattern: false,
        tenantIsolation: false,
        typeScriptStrict: false,
        productionReady: false
      },
      businessLogic: {
        workflowsPreserved: [],
        moduleIntegration: false,
        agentTriggering: false
      },
      errors: [error instanceof Error ? error.message : 'Unknown error during code generation']
    };
  }
}

