/**
 * Validates generated code for Mizan compliance
 */
async function validateMizanCompliance(
  code: string,
  context: MizanAgentContext
): Promise<{
  success: boolean;
  mizanCompliance: {
    threeEnginePattern: boolean;
    tenantIsolation: boolean;
    typeScriptStrict: boolean;
    productionReady: boolean;
  };
  businessLogic: {
    workflowsPreserved: string[];
    moduleIntegration: boolean;
    agentTriggering: boolean;
  };
  errors?: string[];
}> {
  const errors: string[] = [];

  // Check TypeScript strict types
  const hasAnyTypes = /:\s*any\b/.test(code);
  const hasProperTypes = /interface\s+\w+|type\s+\w+/.test(code);

  // Check tenant isolation
  const hasTenantId = /tenantId/.test(code);
  const hasProperQueries = /eq\(.*\.tenantId,/.test(code);

  // Check Three-Engine pattern
  const hasEnginePattern = /KnowledgeEngine|DataEngine|ReasoningEngine/.test(code);

  // Check production readiness
  const hasMockData = /mock|placeholder|TODO/i.test(code);
  const hasErrorHandling = /try\s*{[\s\S]*catch/.test(code);

  // Check business logic preservation
  const workflowsPreserved: string[] = [];
  if (context.moduleType === 'culture' && /Recognition|Engagement/.test(code)) {
    workflowsPreserved.push('CultureRecognition/Engagement');
  }
  if (context.moduleType === 'skills' && /LXP|Learning/.test(code)) {
    workflowsPreserved.push('SkillsLXP');
  }

  if (hasAnyTypes) errors.push('Contains any types - strict TypeScript required');
  if (!hasTenantId) errors.push('Missing tenant isolation');
  if (hasMockData) errors.push('Contains mock data or placeholders');
  if (!hasErrorHandling) errors.push('Missing error handling');

  return {
    success: errors.length === 0,
    mizanCompliance: {
      threeEnginePattern: hasEnginePattern,
      tenantIsolation: hasTenantId && hasProperQueries,
      typeScriptStrict: hasProperTypes && !hasAnyTypes,
      productionReady: !hasMockData && hasErrorHandling
    },
    businessLogic: {
      workflowsPreserved,
      moduleIntegration: workflowsPreserved.length > 0,
      agentTriggering: /trigger|agent/i.test(code)
    },
    errors: errors.length > 0 ? errors : undefined
  };
}

