// Convert entire file to TypeScript with strict types
interface ValidationResult {
  isValid: boolean;
  violations: ViolationDetail[];
  confidence: number;
  mizanCompliance: boolean;
  businessImpact: string;
  recommendations: string[];
}

interface ViolationDetail {
  file: string;
  line: number;
  rule: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  code: string;
  description: string;
  mizanModule?: string;
  workflowImpact?: string;
}

interface PreviousViolation {
  id: string;
  file: string;
  rule: string;
  fixAttempts: number;
  lastAttempt: Date;
  status: 'pending' | 'fixed' | 'failed';
  mizanContext?: {
    module: string;
    workflow: string;
    businessImpact: string;
  };
}

interface RefinementInstruction {
  priority: number;
  instruction: string;
  mizanContext: string;
  businessRationale: string;
  technicalApproach: string;
  validationCriteria: string[];
}

/**
 * Builds refinement instructions for Mizan platform violations
 * Maintains business workflow integrity and strategic alignment
 */
function buildRefinementInstructions(
  validation: ValidationResult, 
  previousViolations: PreviousViolation[]
): RefinementInstruction[] {
  const instructions: RefinementInstruction[] = [];

  // Process each violation with Mizan-specific context
  validation.violations.forEach((violation, index) => {
    const previousAttempts = previousViolations.filter(
      prev => prev.file === violation.file && prev.rule === violation.rule
    );

    const instruction: RefinementInstruction = {
      priority: calculateMizanPriority(violation),
      instruction: generateMizanSpecificInstruction(violation, previousAttempts),
      mizanContext: getMizanModuleContext(violation),
      businessRationale: getBusinessRationale(violation),
      technicalApproach: getTechnicalApproach(violation),
      validationCriteria: getValidationCriteria(violation)
    };

    instructions.push(instruction);
  });

  // Sort by Mizan business priority
  return instructions.sort((a, b) => b.priority - a.priority);
}

function calculateMizanPriority(violation: ViolationDetail): number {
  let priority = 50; // Base priority

  // Critical Mizan architecture violations get highest priority
  if (violation.rule.includes('THREE_ENGINE_ARCHITECTURE')) priority += 40;
  if (violation.rule.includes('TENANT_ISOLATION')) priority += 35;
  if (violation.rule.includes('AGENT_TRIGGERING')) priority += 30;
  if (violation.rule.includes('MODULE_INTEGRATION')) priority += 25;

  // Business workflow impact
  if (violation.workflowImpact?.includes('CultureRecognition')) priority += 20;
  if (violation.workflowImpact?.includes('SkillsLXP')) priority += 20;
  if (violation.workflowImpact?.includes('Performance')) priority += 15;

  // Severity multiplier
  const severityMultiplier = {
    'critical': 2.0,
    'high': 1.5,
    'medium': 1.2,
    'low': 1.0
  };

  return Math.round(priority * severityMultiplier[violation.severity]);
}

function generateMizanSpecificInstruction(
  violation: ViolationDetail, 
  previousAttempts: PreviousViolation[]
): string {
  const baseInstruction = `Fix ${violation.rule} violation in ${violation.file}:${violation.line}`;

  // Add Mizan-specific context
  let mizanGuidance = '';

  if (violation.rule.includes('TYPESCRIPT')) {
    mizanGuidance = `
    MIZAN TYPESCRIPT REQUIREMENTS:
    - Use strict TypeScript types (no 'any')
    - Define interfaces for all data structures
    - Include tenant isolation types (tenantId: string)
    - Follow Three-Engine Architecture patterns
    - Maintain business workflow type safety
    `;
  }

  if (violation.rule.includes('TENANT_ISOLATION')) {
    mizanGuidance = `
    MIZAN TENANT ISOLATION REQUIREMENTS:
    - Include tenantId in ALL database queries
    - Use Drizzle ORM with proper where clauses
    - Validate tenant access in middleware
    - Maintain data separation across all modules
    `;
  }

  if (violation.rule.includes('THREE_ENGINE')) {
    mizanGuidance = `
    MIZAN THREE-ENGINE ARCHITECTURE:
    - KnowledgeEngine: Domain context and best practices
    - DataEngine: Data processing and normalization
    - ReasoningEngine: Analysis and recommendations
    - Maintain engine separation and proper data flow
    `;
  }

  // Add previous attempt context
  if (previousAttempts.length > 0) {
    mizanGuidance += `
    PREVIOUS ATTEMPTS: ${previousAttempts.length}
    - Avoid previous failed approaches
    - Focus on production-ready implementation
    - Ensure complete business workflow preservation
    `;
  }

  return baseInstruction + mizanGuidance;
}

function getMizanModuleContext(violation: ViolationDetail): string {
  if (violation.file.includes('culture')) {
    return 'Culture Analysis Module - Triggers Recognition/Engagement agents';
  }
  if (violation.file.includes('skills')) {
    return 'Skills Analysis Module - Triggers LXP module activation';
  }
  if (violation.file.includes('performance')) {
    return 'Performance Module - Integrates Culture/Skills priorities';
  }
  if (violation.file.includes('structure')) {
    return 'Structure Analysis Module - Triggers Hiring module';
  }
  return 'Core Platform Infrastructure';
}

function getBusinessRationale(violation: ViolationDetail): string {
  return `
  This violation impacts Mizan's strategic business objectives:
  - Platform reliability and production readiness
  - Multi-tenant data security and isolation
  - Business workflow completeness and integrity
  - Cross-module integration and agent triggering
  - Long-term maintainability and scalability
  `;
}

function getTechnicalApproach(violation: ViolationDetail): string {
  let approach = 'Standard technical fix with Mizan compliance: ';

  if (violation.rule.includes('TYPESCRIPT')) {
    approach += `
    1. Define strict TypeScript interfaces
    2. Apply types to function parameters
    3. Ensure tenant isolation types
    4. Validate business workflow types
    `;
  }

  if (violation.rule.includes('DATABASE')) {
    approach += `
    1. Use Drizzle ORM patterns
    2. Include tenantId in all queries
    3. Implement proper error handling
    4. Maintain Three-Engine data flow
    `;
  }

  return approach;
}

function getValidationCriteria(violation: ViolationDetail): string[] {
  const baseCriteria = [
    'Code compiles without TypeScript errors',
    'All tests pass',
    'Production-ready implementation',
    'No mock data or placeholders'
  ];

  const mizanCriteria = [
    'Multi-tenant isolation maintained',
    'Business workflows preserved',
    'Agent triggering mechanisms intact',
    'Three-Engine Architecture compliance',
    'Cross-module integration functional'
  ];

  return [...baseCriteria, ...mizanCriteria];
}

export {
  buildRefinementInstructions,
  type ValidationResult,
  type ViolationDetail,
  type PreviousViolation,
  type RefinementInstruction
};

/**
 * Enhances prompts with violation context for Mizan platform development
 * Maintains strict TypeScript compliance as required by AGENT_CONTEXT_ULTIMATE.md
 * @param {string} originalPrompt - The base prompt to enhance
 * @param {Array<Object>} refinementInstructions - Array of refinement objects with structure: {type: string, description: string, priority: number}
 * @param {string} previousCode - Previously generated code for context
 * @param {number} attempt - Current attempt number for tracking
 * @returns {string} Enhanced prompt with violation context and Mizan-specific requirements
 */
function enhancePromptWithViolations(
  originalPrompt: string,
  refinementInstructions: Array<{
    type: string;
    description: string;
    priority: number;
    mizanModule?: string;
    workflowImpact?: string;
  }>,
  previousCode: string,
  attempt: number
): string {

// scripts/agents/developer-agent-mizan-enhanced.ts
import { z } from 'zod';

// Mizan-compliant TypeScript interfaces for agent system
interface MizanFixPlan {
  id: string;
  tenantId?: string; // For tenant-aware fixes
  confidence: number;
  complexity: 'simple' | 'moderate' | 'complex' | 'enterprise';
  mizanCompliance: boolean;
  businessImpact: 'positive' | 'neutral' | 'negative';
  primaryFix: MizanPrimaryFix;
  mizanArchitecture: MizanArchitectureCompliance;
  businessLogic: MizanBusinessLogic;
  additionalFiles?: MizanAdditionalFile[];
  dependencies: string[];
  migrationSteps: string[];
  testingStrategy: MizanTestingStrategy;
  mizanValidation: MizanValidationResult;
}

interface MizanPrimaryFix {
  file: string;
  startLine: number;
  endLine: number;
  oldCode: string;
  newCode: string;
  explanation: string;
}

interface MizanArchitectureCompliance {
  threeEngineCompliance: boolean;
  agentTriggering: boolean;
  moduleIntegration: boolean;
  tenantIsolation: boolean;
  workflowCompleteness: boolean;
}

interface MizanBusinessLogic {
  affectedModules: string[];
  workflowsRestored: string[];
  strategicAlignment: 'maintained' | 'improved' | 'compromised';
}

interface MizanAdditionalFile {
  path: string;
  content: string;
  reason: string;
}

interface MizanTestingStrategy {
  unitTests: string;
  integrationTests: string;
  businessTests: string;
  tenantTests: string;
}

interface MizanValidationResult {
  platformCompliance: boolean;
  architectureIntegrity: boolean;
  businessWorkflows: 'maintained' | 'improved' | 'broken';
  productionReadiness: boolean;
  confidence: number;
  improvements: string;
}

// Zod schema for runtime validation of fix plan content
const FixPlanContentSchema = z.object({
  confidence: z.number().min(0).max(1),
  complexity: z.enum(['simple', 'moderate', 'complex', 'enterprise']),
  mizanCompliance: z.boolean(),
  businessImpact: z.enum(['positive', 'neutral', 'negative']),
  primaryFix: z.object({
    file: z.string().min(1),
    startLine: z.number().positive(),
    endLine: z.number().positive(),
    oldCode: z.string(),
    newCode: z.string().min(1),
    explanation: z.string().min(10)
  }),
  mizanArchitecture: z.object({
    threeEngineCompliance: z.boolean(),
    agentTriggering: z.boolean(),
    moduleIntegration: z.boolean(),
    tenantIsolation: z.boolean(),
    workflowCompleteness: z.boolean()
  }),
  businessLogic: z.object({
    affectedModules: z.array(z.string()),
    workflowsRestored: z.array(z.string()),
    strategicAlignment: z.enum(['maintained', 'improved', 'compromised'])
  }),
  additionalFiles: z.array(z.object({
    path: z.string().min(1),
    content: z.string().min(1),
    reason: z.string().min(10)
  })).optional(),
  dependencies: z.array(z.string()),
  migrationSteps: z.array(z.string()),
  testingStrategy: z.object({
    unitTests: z.string().min(5),
    integrationTests: z.string().min(5),
    businessTests: z.string().min(5),
    tenantTests: z.string().min(5)
  }),
  mizanValidation: z.object({
    platformCompliance: z.boolean(),
    architectureIntegrity: z.boolean(),
    businessWorkflows: z.enum(['maintained', 'improved', 'broken']),
    productionReadiness: z.boolean(),
    confidence: z.number().min(0).max(100),
    improvements: z.string()
  })
});

// Production-ready error classes for Mizan agent system
class MizanAgentError extends Error {
  constructor(
    message: string,
    public code: string,
    public details?: Record<string, unknown>
  ) {
    super(message);
    this.name = 'MizanAgentError';
  }
}

class FixPlanParseError extends MizanAgentError {
  constructor(message: string, details?: Record<string, unknown>) {
    super(message, 'FIX_PLAN_PARSE_ERROR', details);
  }
}

class FixPlanValidationError extends MizanAgentError {
  constructor(message: string, details?: Record<string, unknown>) {
    super(message, 'FIX_PLAN_VALIDATION_ERROR', details);
  }
}

/**
 * Parses and validates fix plan content with strict TypeScript types
 * Follows Mizan's Three-Engine Architecture pattern:
 * 1. Knowledge Engine: Understands fix plan structure and validation rules
 * 2. Data Engine: Processes and normalizes the raw fix plan content
 * 3. Reasoning Engine: Validates business logic and Mizan compliance
 * 
 * @param fixPlanContent - Raw fix plan content (string or object)
 * @param tenantId - Optional tenant ID for tenant-aware processing
 * @returns Validated and typed MizanFixPlan
 * @throws FixPlanParseError for parsing issues
 * @throws FixPlanValidationError for validation failures
 */
function parseFixPlan(
  fixPlanContent: string | Record<string, unknown>,
  tenantId?: string
): MizanFixPlan {
  try {
    // Knowledge Engine: Understand the input format and requirements
    let parsedContent: Record<string, unknown>;

    if (typeof fixPlanContent === 'string') {
      try {
        parsedContent = JSON.parse(fixPlanContent);
      } catch (jsonError) {
        throw new FixPlanParseError(
          'Invalid JSON format in fix plan content',
          { 
            originalContent: fixPlanContent.substring(0, 200),
            jsonError: jsonError instanceof Error ? jsonError.message : 'Unknown JSON error'
          }
        );
      }
    } else if (typeof fixPlanContent === 'object' && fixPlanContent !== null) {
      parsedContent = fixPlanContent;
    } else {
      throw new FixPlanParseError(
        'Fix plan content must be a string or object',
        { 
          receivedType: typeof fixPlanContent,
          receivedValue: fixPlanContent
        }
      );
    }

    // Data Engine: Process and normalize the parsed content
    const processedContent = {
      ...parsedContent,
      id: parsedContent.id || generateFixPlanId(),
      tenantId: tenantId || parsedContent.tenantId || undefined
    };

    // Reasoning Engine: Validate against Mizan business rules and schema
    const validatedContent = FixPlanContentSchema.parse(processedContent);

    // Additional Mizan-specific business logic validation
    validateMizanBusinessRules(validatedContent);

    // Return fully typed and validated fix plan
    return {
      ...validatedContent,
      id: processedContent.id as string,
      tenantId: processedContent.tenantId as string | undefined
    } as MizanFixPlan;

  } catch (error) {
    if (error instanceof z.ZodError) {
      throw new FixPlanValidationError(
        'Fix plan content failed schema validation',
        {
          zodErrors: error.errors,
          receivedContent: fixPlanContent
        }
      );
    }

    if (error instanceof MizanAgentError) {
      throw error;
    }

    throw new FixPlanParseError(
      'Unexpected error parsing fix plan',
      {
        originalError: error instanceof Error ? error.message : 'Unknown error',
        receivedContent: typeof fixPlanContent === 'string' 
          ? fixPlanContent.substring(0, 200) 
          : JSON.stringify(fixPlanContent).substring(0, 200)
      }
    );
  }
}

/**
 * Validates Mizan-specific business rules for fix plans
 * Ensures fix plans maintain platform integrity and workflow completeness
 */
function validateMizanBusinessRules(fixPlan: z.infer<typeof FixPlanContentSchema>): void {
  // Rule 1: High-impact fixes must have high confidence
  if (fixPlan.businessImpact === 'positive' && fixPlan.confidence < 0.8) {
    throw new FixPlanValidationError(
      'High-impact fixes require confidence >= 0.8',
      { confidence: fixPlan.confidence, businessImpact: fixPlan.businessImpact }
    );
  }

  // Rule 2: Complex fixes must include migration steps
  if (fixPlan.complexity === 'complex' || fixPlan.complexity === 'enterprise') {
    if (!fixPlan.migrationSteps || fixPlan.migrationSteps.length === 0) {
      throw new FixPlanValidationError(
        'Complex fixes must include migration steps',
        { complexity: fixPlan.complexity, migrationSteps: fixPlan.migrationSteps }
      );
    }
  }

  // Rule 3: Mizan architecture compliance validation
  if (fixPlan.mizanCompliance && !fixPlan.mizanArchitecture.workflowCompleteness) {
    throw new FixPlanValidationError(
      'Mizan-compliant fixes must maintain workflow completeness',
      { mizanArchitecture: fixPlan.mizanArchitecture }
    );
  }

  // Rule 4: Production readiness validation
  if (!fixPlan.mizanValidation.productionReadiness && fixPlan.mizanValidation.confidence > 80) {
    throw new FixPlanValidationError(
      'High-confidence fixes must be production-ready',
      { 
        productionReadiness: fixPlan.mizanValidation.productionReadiness,
        confidence: fixPlan.mizanValidation.confidence
      }
    );
  }

  // Rule 5: Business workflow integrity
  if (fixPlan.businessLogic.strategicAlignment === 'compromised') {
    throw new FixPlanValidationError(
      'Fixes cannot compromise strategic alignment',
      { strategicAlignment: fixPlan.businessLogic.strategicAlignment }
    );
  }
}

/**
 * Generates a unique fix plan ID following Mizan conventions
 */
function generateFixPlanId(): string {
  const timestamp = Date.now();
  const random = Math.random().toString(36).substring(2, 8);
  return `mizan-fix-${timestamp}-${random}`;
}

/**
 * Validates if a fix plan affects critical Mizan modules
 * Returns impact assessment for business workflow planning
 */
function assessMizanModuleImpact(fixPlan: MizanFixPlan): {
  criticalModules: string[];
  workflowRisk: 'low' | 'medium' | 'high';
  integrationComplexity: 'simple' | 'moderate' | 'complex';
} {
  const criticalMizanModules = [
    'Culture', 'Structure', 'Skills', 'Performance', 'Hiring',
    'LXP', 'Talent', 'Bonus', 'Recognition', 'Engagement'
  ];

  const affectedCriticalModules = fixPlan.businessLogic.affectedModules
    .filter(module => criticalMizanModules.includes(module));

  let workflowRisk: 'low' | 'medium' | 'high' = 'low';
  let integrationComplexity: 'simple' | 'moderate' | 'complex' = 'simple';

  if (affectedCriticalModules.length > 3) {
    workflowRisk = 'high';
    integrationComplexity = 'complex';
  } else if (affectedCriticalModules.length > 1) {
    workflowRisk = 'medium';
    integrationComplexity = 'moderate';
  }

  // Special case: Culture module affects Recognition/Engagement agents
  if (affectedCriticalModules.includes('Culture')) {
    workflowRisk = workflowRisk === 'low' ? 'medium' : 'high';
  }

  // Special case: Skills module affects LXP triggering
  if (affectedCriticalModules.includes('Skills')) {
    workflowRisk = workflowRisk === 'low' ? 'medium' : 'high';
  }

  return {
    criticalModules: affectedCriticalModules,
    workflowRisk,
    integrationComplexity
  };
}

export {
  parseFixPlan,
  assessMizanModuleImpact,
  type MizanFixPlan,
  type MizanPrimaryFix,
  type MizanArchitectureCompliance,
  type MizanBusinessLogic,
