/**
 * Extracts file paths from description text for Mizan platform analysis
 * Used by AI agents for code analysis and security validation
 * @param description - Text description containing potential file paths
 * @returns Array of extracted file paths relative to Mizan-1/ root
 */
function extractFilePaths(description: string): string[] {
  if (!description || typeof description !== 'string') {
    throw new Error('Description must be a non-empty string');
  }

  try {
    // Mizan-specific path patterns for platform files
    const mizanPathPatterns = [
      // Frontend paths (Next.js 14 App Router)
      /frontend\/src\/app\/[^\s]+\.tsx?/g,
      /frontend\/src\/components\/[^\s]+\.tsx?/g,
      /frontend\/src\/lib\/[^\s]+\.ts/g,
      /frontend\/src\/types\/[^\s]+\.ts/g,

      // Backend paths (Express.js + Drizzle ORM)
      /backend\/src\/routes\/[^\s]+\.ts/g,
      /backend\/src\/services\/[^\s]+\.ts/g,
      /backend\/src\/db\/schema\/[^\s]+\.ts/g,
      /backend\/src\/ai\/[^\s]+\.ts/g,

      // Agent scripts
      /scripts\/agents\/[^\s]+\.(?:js|ts)/g,

      // General file patterns
      /[a-zA-Z0-9_-]+\/[a-zA-Z0-9_/-]+\.[a-zA-Z0-9]+/g
    ];

    const extractedPaths: string[] = [];

    for (const pattern of mizanPathPatterns) {
      const matches = description.match(pattern);
      if (matches) {
        extractedPaths.push(...matches);
      }
    }

    // Remove duplicates and validate Mizan project structure
    const uniquePaths = [...new Set(extractedPaths)];

    // Filter for valid Mizan platform paths only
    const validMizanPaths = uniquePaths.filter(path => {
      return path.startsWith('frontend/') || 
             path.startsWith('backend/') || 
             path.startsWith('scripts/') ||
             path.startsWith('docs/');
    });

    return validMizanPaths.sort();

  } catch (error) {
    console.error('Error extracting file paths from description:', error);
