  phaseName: MizanDevelopmentPhase
): MizanIssueAnalysis {
  // Input validation with comprehensive error handling
  if (!description || typeof description !== 'string' || description.trim().length === 0) {
    throw new MizanValidationError('Issue description is required and must be a non-empty string');
  }

  if (!phaseName || !Object.values(MizanDevelopmentPhase).includes(phaseName)) {
