/**
 * Determines priority for Mizan platform development tasks based on business impact
 * and development phase. Supports multi-agent workflow prioritization.
 * 
 * @param {string} description - Task description or violation details
 * @param {number} phaseNumber - Development phase (1-4: Analysis, Design, Implementation, Testing)
 * @returns {'critical'|'high'|'medium'|'low'} Priority level for agent processing
 */
function determinePriority(description, phaseNumber) {
  // Validate input parameters with strict type checking
  if (typeof description !== 'string' || description.trim().length === 0) {
    throw new Error('determinePriority: description must be a non-empty string');
  }

  if (typeof phaseNumber !== 'number' || !Number.isInteger(phaseNumber) || phaseNumber < 1 || phaseNumber > 4) {
    throw new Error('determinePriority: phaseNumber must be an integer between 1 and 4');
  }

  // Mizan-specific priority keywords for business-critical components
  const criticalKeywords = [
    'tenant isolation', 'tenantId', 'multi-tenant',
    'three-engine', 'knowledge engine', 'data engine', 'reasoning engine',
    'culture agent', 'recognition agent', 'engagement agent',
    'skills analysis', 'lxp trigger', 'module integration',
    'performance module', 'hiring module', 'bonus module',
    'authentication', 'authorization', 'security',
    'database', 'drizzle', 'sql injection',
    'production', 'deployment', 'critical path'
  ];

  const highKeywords = [
    'typescript', 'type safety', 'error handling',
    'api route', 'next.js', 'app router',
    'workflow', 'business logic', 'integration',
    'validation', 'schema', 'data flow',
    'dashboard', 'ui component', 'user experience'
  ];

  const mediumKeywords = [
    'styling', 'css', 'tailwind', 'design',
    'optimization', 'performance', 'caching',
    'logging', 'monitoring', 'analytics',
    'documentation', 'comments', 'readme'
  ];

  // Convert description to lowercase for case-insensitive matching
  const lowerDescription = description.toLowerCase();

  // Determine base priority from keywords
  let basePriority = 'low';

  if (criticalKeywords.some(keyword => lowerDescription.includes(keyword))) {
    basePriority = 'critical';
  } else if (highKeywords.some(keyword => lowerDescription.includes(keyword))) {
    basePriority = 'high';
  } else if (mediumKeywords.some(keyword => lowerDescription.includes(keyword))) {
    basePriority = 'medium';
  }

  // Adjust priority based on development phase
  // Earlier phases (1-2) get higher priority for foundational issues
  // Later phases (3-4) maintain priority for implementation/testing issues
  if (phaseNumber <= 2 && basePriority !== 'critical') {
    // Boost priority for foundational issues in early phases
    const priorityLevels = ['low', 'medium', 'high', 'critical'];
    const currentIndex = priorityLevels.indexOf(basePriority);
    if (currentIndex < priorityLevels.length - 1) {
      basePriority = priorityLevels[currentIndex + 1];
    }
  }

  return basePriority;
}

