/**
 * Writes generated code to file with Mizan platform compliance validation
 * Ensures all generated code follows Three-Engine Architecture and tenant isolation patterns
 */
async function writeGeneratedCode(
  filePath: string, 
  code: string, 
  description: string,
  metadata?: Partial<MizanCodeMetadata>
): Promise<CodeGenerationResult> {
  try {
    // Validate Mizan compliance before writing
    const validationResult = validateMizanCompliance(code, filePath);
    if (!validationResult.isCompliant) {
      throw new Error(`Generated code violates Mizan standards: ${validationResult.violations.join(', ')}`);
    }

    // Ensure directory exists
    const dir = dirname(filePath);
    await fs.mkdir(dir, { recursive: true });

    // Add Mizan header comment to generated code
    const mizanHeader = generateMizanHeader(description, metadata);
    const compliantCode = `${mizanHeader}\n\n${code}`;

    // Write file with proper encoding
    await fs.writeFile(filePath, compliantCode, 'utf8');

    // Log generation for audit trail
    logCodeGeneration(filePath, description, metadata);

    return {
      success: true,
      filePath,
      timestamp: new Date()
    };

  } catch (error) {
    console.error(`Failed to write generated code to ${filePath}:`, error);
    return {
      success: false,
      filePath,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: new Date()
    };
  }
}

/**
 * Validates that generated code follows Mizan platform standards
 */
function validateMizanCompliance(code: string, filePath: string): {
  isCompliant: boolean;
  violations: string[];
} {
  const violations: string[] = [];

  // Check for TypeScript compliance
  if (filePath.endsWith('.js') && !filePath.includes('config')) {
    violations.push('File should use .ts extension for TypeScript compliance');
  }

  // Check for 'any' types
  if (code.includes(': any') || code.includes('<any>')) {
    violations.push('Code contains forbidden "any" types');
  }

  // Check for tenant isolation in database queries
  if (code.includes('db.select()') && !code.includes('tenantId')) {
    violations.push('Database queries must include tenant isolation');
  }

  // Check for proper error handling
  if (code.includes('await ') && !code.includes('try') && !code.includes('catch')) {
    violations.push('Async operations must include proper error handling');
  }

  // Check for mock data
  if (code.includes('mock') || code.includes('TODO') || code.includes('placeholder')) {
    violations.push('Code contains forbidden mock data or placeholders');
  }

  // Check for Three-Engine pattern in AI modules
  if ((filePath.includes('culture') || filePath.includes('skills') || filePath.includes('structure')) 
      && code.includes('Service') 
      && !code.includes('KnowledgeEngine') 
      && !code.includes('DataEngine') 
      && !code.includes('ReasoningEngine')) {
    violations.push('AI modules must implement Three-Engine Architecture');
  }

  return {
    isCompliant: violations.length === 0,
    violations
  };
}

/**
 * Generates Mizan-compliant header comment for generated files
 */
function generateMizanHeader(description: string, metadata?: Partial<MizanCodeMetadata>): string {
  const timestamp = new Date().toISOString();
  const moduleInfo = metadata?.moduleType ? ` - ${metadata.moduleType.toUpperCase()} MODULE` : '';

  return `/**
 * MIZAN PLATFORM${moduleInfo} - GENERATED CODE
 * 
 * Description: ${description}
 * Generated: ${timestamp}
 * Architecture: Three-Engine AI System
 * Compliance: Multi-tenant isolation, TypeScript strict types
 * 
 * CRITICAL: This code follows Mizan platform standards:
 * - Three-Engine Architecture (Knowledge  Data  Reasoning)
 * - Multi-tenant isolation (tenantId in all queries)
 * - Agent triggering mechanisms (Culture  Recognition/Engagement)
 * - Module integration workflows (Skills  LXP activation)
 * - Production-ready implementation (NO mock data, NO placeholders)
 * 
 * @generated by Mizan Developer Agent
 * @compliance AGENT_CONTEXT_ULTIMATE.md
 */`;
}

/**
 * Logs code generation for audit and debugging purposes
 */
function logCodeGeneration(
  filePath: string, 
  description: string, 
  metadata?: Partial<MizanCodeMetadata>
): void {
  const logEntry = {
    timestamp: new Date().toISOString(),
    filePath,
    description,
    metadata: {
      moduleType: metadata?.moduleType || 'unknown',
      hasThreeEnginePattern: metadata?.hasThreeEnginePattern || false,
      hasTenantIsolation: metadata?.hasTenantIsolation || false,
      hasAgentTriggering: metadata?.hasAgentTriggering || false,
      generatedBy: metadata?.generatedBy || 'developer-agent-mizan-enhanced'
    }
  };

  console.log('[MIZAN CODE GENERATION]', JSON.stringify(logEntry, null, 2));
}

