/**
 * Determines if 'any' type should be allowed in specific Mizan contexts
 * Maintains strict typing while allowing necessary flexibility for AI engines
 */
function shouldAllowAnyType(
  code: string, 
  match: string, 
  fileName: string, 
  matchIndex: number
): ValidationResult {
  const fileContext = analyzeMizanFileContext(fileName);

  // Mizan Platform: NO 'any' types allowed in core business logic
  if (fileContext.isMizanCore) {
    return {
      isValid: false,
      reason: "Mizan core modules require strict TypeScript typing for production readiness",
      mizanContext: {
        module: determineModuleType(fileName),
        tenantIsolated: checkTenantIsolation(code),
      }
    };
  }

  // Three-Engine Architecture: Limited 'any' for AI processing
  if (isThreeEngineContext(code, fileName)) {
    const engineType = determineEngineType(code, fileName);

    // Knowledge Engine: Allow 'any' for external API responses
    if (engineType === 'knowledge' && isExternalAPIContext(code, matchIndex)) {
      return {
        isValid: true,
        reason: "Knowledge Engine external API integration requires flexible typing",
        mizanContext: {
          module: determineModuleType(fileName),
          engineType: 'knowledge',
          tenantIsolated: checkTenantIsolation(code),
        }
      };
    }

    // Data Engine: Allow 'any' for raw data processing input
    if (engineType === 'data' && isRawDataProcessingContext(code, matchIndex)) {
      return {
        isValid: true,
        reason: "Data Engine raw input processing requires flexible typing before validation",
        mizanContext: {
          module: determineModuleType(fileName),
          engineType: 'data',
          tenantIsolated: checkTenantIsolation(code),
        }
      };
    }

    // Reasoning Engine: Strict typing required
    return {
      isValid: false,
      reason: "Reasoning Engine must use strict types for reliable analysis",
      mizanContext: {
        module: determineModuleType(fileName),
        engineType: 'reasoning',
        tenantIsolated: checkTenantIsolation(code),
      }
    };
  }

  // Agent triggering systems: Strict typing required
  if (isAgentTriggerContext(fileName)) {
    return {
      isValid: false,
      reason: "Agent triggering systems require strict typing for reliable workflow execution",
      mizanContext: {
        module: 'agent',
        tenantIsolated: checkTenantIsolation(code),
      }
    };
  }

  // Default: No 'any' types allowed
  return {
    isValid: false,
    reason: "Mizan Platform requires strict TypeScript typing for production readiness",
    mizanContext: {
      module: 'other',
      tenantIsolated: checkTenantIsolation(code),
    }
  };
}

/**
 * Analyzes file context within Mizan architecture
 */
function analyzeMizanFileContext(fileName: string): FileContext {
  const filePath = fileName.toLowerCase();

  return {
    fileName,
    filePath,
    isAgentFile: filePath.includes('/agents/') || filePath.includes('agent'),
    isMizanCore: (
      filePath.includes('/culture/') ||
      filePath.includes('/skills/') ||
      filePath.includes('/structure/') ||
      filePath.includes('/performance/') ||
      filePath.includes('/hiring/') ||
      filePath.includes('/lxp/') ||
      filePath.includes('/talent/') ||
      filePath.includes('/bonus/')
    ),
    moduleType: determineModuleType(fileName),
  };
}

/**
 * Determines Mizan module type from file path
 */
function determineModuleType(fileName: string): 'culture' | 'skills' | 'structure' | 'performance' | 'hiring' | 'lxp' | 'talent' | 'bonus' | 'agent' | 'other' {
  const filePath = fileName.toLowerCase();

  if (filePath.includes('/culture/') || filePath.includes('culture')) return 'culture';
  if (filePath.includes('/skills/') || filePath.includes('skills')) return 'skills';
  if (filePath.includes('/structure/') || filePath.includes('structure')) return 'structure';
  if (filePath.includes('/performance/') || filePath.includes('performance')) return 'performance';
  if (filePath.includes('/hiring/') || filePath.includes('hiring')) return 'hiring';
  if (filePath.includes('/lxp/') || filePath.includes('lxp')) return 'lxp';
  if (filePath.includes('/talent/') || filePath.includes('talent')) return 'talent';
  if (filePath.includes('/bonus/') || filePath.includes('bonus')) return 'bonus';
  if (filePath.includes('/agents/') || filePath.includes('agent')) return 'agent';

  return 'other';
}

/**
 * Checks if code context is within Three-Engine Architecture
 */
function isThreeEngineContext(code: string, fileName: string): boolean {
  const enginePatterns = [
    'KnowledgeEngine',
    'DataEngine', 
    'ReasoningEngine',
    'knowledgeEngine',
    'dataEngine',
    'reasoningEngine'
  ];

  return enginePatterns.some(pattern => 
    code.includes(pattern) || fileName.includes(pattern)
  );
}

/**
 * Determines which engine type based on context
 */
function determineEngineType(code: string, fileName: string): 'knowledge' | 'data' | 'reasoning' | null {
  if (code.includes('KnowledgeEngine') || fileName.includes('Knowledge')) return 'knowledge';
  if (code.includes('DataEngine') || fileName.includes('Data')) return 'data';
  if (code.includes('ReasoningEngine') || fileName.includes('Reasoning')) return 'reasoning';
  return null;
}

/**
 * Checks if context is external API integration
 */
function isExternalAPIContext(code: string, matchIndex: number): boolean {
  const contextWindow = code.substring(Math.max(0, matchIndex - 200), matchIndex + 200);
  const externalAPIPatterns = [
    'fetch(',
    'axios.',
    'openai.',
    'anthropic.',
    'external',
    'api',
    'response.json()',
    'await.*api'
  ];

  return externalAPIPatterns.some(pattern => 
    new RegExp(pattern, 'i').test(contextWindow)
  );
}

/**
 * Checks if context is raw data processing
 */
function isRawDataProcessingContext(code: string, matchIndex: number): boolean {
  const contextWindow = code.substring(Math.max(0, matchIndex - 200), matchIndex + 200);
  const rawDataPatterns = [
    'rawData',
    'unprocessed',
    'input.*data',
    'process.*raw',
    'validate.*input'
  ];

  return rawDataPatterns.some(pattern => 
    new RegExp(pattern, 'i').test(contextWindow)
  );
}

/**
 * Checks if context is agent triggering system
 */
function isAgentTriggerContext(fileName: string): boolean {
  const triggerPatterns = [
    'trigger',
    'agent',
    'recognition',
    'engagement',
    'workflow'
  ];

  return triggerPatterns.some(pattern => 
    fileName.toLowerCase().includes(pattern)
  );
}

/**
 * Checks if code maintains tenant isolation
 */
function checkTenantIsolation(code: string): boolean {
  const tenantPatterns = [
    'tenantId',
    'tenant_id',
    'eq.*tenant',
    'where.*tenant'
  ];

  // If code contains database queries, it should have tenant isolation
  const hasDBQuery = /\.(select|insert|update|delete)\s*\(/.test(code);
  if (!hasDBQuery) return true; // Not a DB query, tenant isolation not required

  return tenantPatterns.some(pattern => 
    new RegExp(pattern, 'i').test(code)
  );
}

