/**
 * Calculates the line number for a given position in code string
 * @param {string} code - The source code string to analyze
 * @param {number} position - The character position within the code
 * @returns {number} The line number (1-based) where the position occurs
 * @throws {Error} If parameters are invalid or position is out of bounds
 */
function getLineNumber(code, position) {
  // Validate input parameters with strict type checking
  if (typeof code !== 'string') {
    throw new Error('MIZAN_VALIDATOR_ERROR: code parameter must be a string');
  }

  if (typeof position !== 'number' || position < 0) {
    throw new Error('MIZAN_VALIDATOR_ERROR: position parameter must be a non-negative number');
  }

  if (position > code.length) {
    throw new Error('MIZAN_VALIDATOR_ERROR: position exceeds code length');
  }

/**
 * Prints validation results with strict typing for Mizan platform compliance
 * @param {ValidationResult[]} results - Array of validation results
 * @param {string} fileName - Name of the file being validated
 * @returns {void}
 */
function printValidationResults(results, fileName) {
  // Type validation for production safety
  if (!Array.isArray(results)) {
    throw new Error(`Invalid results parameter: expected array, got ${typeof results}`);
  }

  if (typeof fileName !== 'string') {
    throw new Error(`Invalid fileName parameter: expected string, got ${typeof fileName}`);
  }

