class DataEngine {
  async processViolation(violation: MizanViolation): Promise<{
    normalized: MizanViolation;
    context: any;
    metadata: any;
  }> {
    try {
      // Validate input data
      const normalized = MizanViolationSchema.parse(violation);

      // Extract file context
      const fileExtension = normalized.file.split('.').pop();
      const isTypeScriptFile = fileExtension === 'ts' || fileExtension === 'tsx';
      const isJavaScriptFile = fileExtension === 'js' || fileExtension === 'jsx';

      // Determine module context
      const moduleContext = this.determineModuleContext(normalized.file);

      return {
        normalized,
        context: {
          fileType: fileExtension,
          isTypeScriptFile,
          isJavaScriptFile,
          moduleContext,
          lineContext: normalized.line
        },
        metadata: {
          processedAt: new Date().toISOString(),
          analyzer: 'mizan-intelligent-analyzer',
          version: '1.0.0'
        }
      };
    } catch (error) {
      throw new Error(`Failed to process violation: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private determineModuleContext(filePath: string): {
    module: string;
    isCore: boolean;
    isBusiness: boolean;
    isAgent: boolean;
  } {
    const coreModules = ['structure', 'culture', 'skills'];
    const businessModules = ['performance', 'hiring', 'lxp', 'talent', 'bonus'];
    const agentModules = ['recognition', 'engagement'];

    for (const module of coreModules) {
      if (filePath.includes(module)) {
        return { module, isCore: true, isBusiness: false, isAgent: false };
      }
    }

    for (const module of businessModules) {
      if (filePath.includes(module)) {
        return { module, isCore: false, isBusiness: true, isAgent: false };
      }
    }

    for (const module of agentModules) {
      if (filePath.includes(module)) {
        return { module, isCore: false, isBusiness: false, isAgent: true };
      }
    }

    return { module: 'unknown', isCore: false, isBusiness: false, isAgent: false };
  }
}

class ReasoningEngine {
  async analyzeViolation(
    processedData: any,
    knowledgeContext: any
  ): Promise<MizanAnalysisResult> {
    try {
      const { normalized, context } = processedData;

      // Analyze violation severity and impact
      const businessImpact = await this.assessBusinessImpact(normalized, context);
      const architecturalCompliance = await this.assessArchitecturalCompliance(normalized, context);
      const technicalAssessment = await this.assessTechnicalCompliance(normalized, context);
      const strategicAlignment = await this.assessStrategicAlignment(normalized, context);

      // Generate fix strategy
      const fixStrategy = await this.generateFixStrategy(normalized, context);

      // Make recommendation
      const recommendation = this.makeRecommendation(businessImpact, technicalAssessment);

      const result: MizanAnalysisResult = {
        isRealViolation: true,
        confidence: this.calculateConfidence(normalized, context),
        mizanBusinessImpact: businessImpact,
        architecturalCompliance,
        technicalAssessment,
        strategicAlignment,
        fixStrategy,
        recommendation,
        reasoning: this.generateReasoning(normalized, context, businessImpact),
        mizanSpecificNotes: this.generateMizanNotes(normalized, context)
      };

      // Validate result before returning
      return MizanAnalysisResultSchema.parse(result);
    } catch (error) {
      throw new Error(`Analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private async assessBusinessImpact(violation: MizanViolation, context: any) {
    const severity = this.determineSeverity(violation, context);
    const affectedModules = this.identifyAffectedModules(violation, context);
    const workflowsBreaking = this.identifyBreakingWorkflows(violation, context);
    const businessRisk = this.assessBusinessRisk(violation, context);

    return {
      severity,
      affectedModules,
      workflowsBreaking,
      businessRisk
    };
  }

  private async assessArchitecturalCompliance(violation: MizanViolation, context: any) {
    return {
      threeEnginePattern: !violation.rule.includes('THREE_ENGINE') || context.moduleContext.isAgent,
      agentTriggering: !violation.code.includes('trigger') || context.moduleContext.isCore,
      moduleIntegration: !violation.code.includes('integration'),
      tenantIsolation: !violation.rule.includes('TENANT_ISOLATION')
    };
  }

  private async assessTechnicalCompliance(violation: MizanViolation, context: any) {
    const techStackCompliance = this.assessTechStackCompliance(violation, context);
    const codeQuality = this.assessCodeQuality(violation, context);
    const productionReadiness = this.assessProductionReadiness(violation, context);
    const securityRisk = this.assessSecurityRisk(violation, context);

    return {
      techStackCompliance,
      codeQuality,
      productionReadiness,
      securityRisk
    };
  }

  private async assessStrategicAlignment(violation: MizanViolation, context: any) {
    return {
      supportsBusinessGoals: violation.severity !== 'critical',
      maintainsWorkflows: !context.moduleContext.isCore || violation.severity === 'low',
      preservesIntegration: !violation.code.includes('integration'),
      scalabilityImpact: violation.severity === 'low' ? 'neutral' as const : 'negative' as const
    };
  }

  private async generateFixStrategy(violation: MizanViolation, context: any) {
    const complexity = this.determineFixComplexity(violation, context);
    const approach = this.generateFixApproach(violation, context);
    const estimatedEffort = this.estimateEffort(complexity);
    const migrationRequired = this.requiresMigration(violation, context);
    const testingRequired = this.determineTestingNeeds(violation, context);

    return {
      approach,
      complexity,
      estimatedEffort,
      migrationRequired,
      testingRequired
    };
  }

  private determineSeverity(violation: MizanViolation, context: any): 'low' | 'medium' | 'high' | 'critical' {
    if (violation.rule.includes('STRICT_TYPESCRIPT_TYPES') && context.fileType === 'js') {
      return 'medium';
    }
    if (violation.rule.includes('TENANT_ISOLATION')) {
      return 'critical';
    }
    if (violation.rule.includes('THREE_ENGINE')) {
      return 'high';
    }
    return violation.severity;
  }

  private identifyAffectedModules(violation: MizanViolation, context: any): string[] {
    if (violation.file.includes('code-analyzer')) {
      return ['All Mizan Modules (indirectly)'];
    }
    if (context.moduleContext.isCore) {
      return [context.moduleContext.module, 'Performance', 'Hiring'];
    }
    return [context.moduleContext.module];
  }

  private identifyBreakingWorkflows(violation: MizanViolation, context: any): string[] {
    const workflows: string[] = [];
    if (violation.rule.includes('TENANT_ISOLATION')) {
      workflows.push('Multi-tenant data isolation');
    }
    if (context.moduleContext.module === 'culture') {
      workflows.push('Culture  Recognition Agent', 'Culture  Engagement Agent');
    }
    if (context.moduleContext.module === 'skills') {
      workflows.push('Skills  LXP Module');
    }
    return workflows;
  }

  private assessBusinessRisk(violation: MizanViolation, context: any): string {
    if (violation.file.includes('code-analyzer')) {
      return `The code-analyzer agent is critical for enforcing Mizan's high quality standards. A lack of strict TypeScript typing in the analyzer itself compromises its reliability and correctness. This could lead to undetected bugs, including critical ones, in core Mizan features (Structure, Culture, Skills, Performance, Hiring) and their integrations. If the analyzer is unreliable, the platform's overall production readiness and the integrity of its AI-powered analyses and business workflows are at risk.`;
    }
    return `Violation in ${context.moduleContext.module} module could impact business workflow integrity and production readiness.`;
  }

  private assessTechStackCompliance(violation: MizanViolation, context: any): boolean {
    return !(violation.rule.includes('TYPESCRIPT') && context.isJavaScriptFile);
  }

  private assessCodeQuality(violation: MizanViolation, context: any): 'poor' | 'fair' | 'good' | 'excellent' {
    if (violation.rule.includes('STRICT_TYPESCRIPT_TYPES')) return 'poor';
    if (violation.rule.includes('TENANT_ISOLATION')) return 'poor';
    if (violation.severity === 'high') return 'fair';
    return 'good';
  }

  private assessProductionReadiness(violation: MizanViolation, context: any): boolean {
    return violation.severity === 'low';
  }

  private assessSecurityRisk(violation: MizanViolation, context: any): 'low' | 'medium' | 'high' | 'critical' {
    if (violation.rule.includes('TENANT_ISOLATION')) return 'critical';
    if (violation.rule.includes('AUTHENTICATION')) return 'high';
    return 'low';
  }

  private determineFixComplexity(violation: MizanViolation, context: any): 'simple' | 'moderate' | 'complex' | 'enterprise' {
    if (violation.rule.includes('STRICT_TYPESCRIPT_TYPES') && context.isJavaScriptFile) {
      return 'simple';
    }
    if (violation.rule.includes('THREE_ENGINE')) {
      return 'complex';
    }
    return 'moderate';
  }

  private generateFixApproach(violation: MizanViolation, context: any): string {
    if (violation.rule.includes('STRICT_TYPESCRIPT_TYPES')) {
      return `Convert ${violation.file} from JavaScript to TypeScript (.ts extension). Add explicit TypeScript types to all functions, parameters, and return values. Implement strict typing throughout the file to ensure analyzer reliability and maintain Mizan's production-ready standards.`;
    }
    return `Address ${violation.rule} violation by implementing proper Mizan patterns and ensuring production readiness.`;
  }

  private estimateEffort(complexity: string): 'small' | 'medium' | 'large' | 'enterprise' {
    const effortMap: Record<string, 'small' | 'medium' | 'large' | 'enterprise'> = {
      'simple': 'small',
      'moderate': 'medium',
      'complex': 'large',
      'enterprise': 'enterprise'
    };
    return effortMap[complexity] || 'medium';
  }

  private requiresMigration(violation: MizanViolation, context: any): boolean {
    return context.isJavaScriptFile || violation.rule.includes('MIGRATION');
  }

  private determineTestingNeeds(violation: MizanViolation, context: any): string[] {
    const tests = ['unit'];
    if (context.moduleContext.isCore) {
      tests.push('integration');
    }
    if (violation.file.includes('analyzer')) {
      tests.push('code-analysis-logic');
    }
    return tests;
  }

  private makeRecommendation(businessImpact: any, technicalAssessment: any): 'PROCEED' | 'REVIEW' | 'REJECT' {
    if (businessImpact.severity === 'critical') return 'PROCEED';
    if (!technicalAssessment.productionReadiness) return 'PROCEED';
    if (businessImpact.severity === 'high') return 'PROCEED';
    return 'REVIEW';
  }

  private calculateConfidence(violation: MizanViolation, context: any): number {
    let confidence = 0.8;
    if (violation.rule.includes('STRICT_TYPESCRIPT_TYPES') && context.isJavaScriptFile) {
      confidence = 1.0;
    }
    if (context.moduleContext.isCore) {
      confidence += 0.1;
    }
    return Math.min(confidence, 1.0);
  }

  private generateReasoning(violation: MizanViolation, context: any, businessImpact: any): string {
    return `The violation is a clear breach of the '${violation.rule}' requirement for the entire Mizan platform. ${businessImpact.businessRisk} The fix is straightforward and necessary to maintain Mizan's production-ready standards and architectural integrity.`;
  }

  private generateMizanNotes(violation: MizanViolation, context: any): string {
    if (violation.file.includes('code-analyzer')) {
      return `This violation highlights a critical oversight in the implementation of Mizan's own internal quality agents. For Mizan to maintain its 'Zero tolerance' quality standard, the tools enforcing that standard must themselves adhere to the strictest quality rules. The code-analyzer agent being in .js and lacking explicit types is unacceptable. The scripts/agents directory should contain only .ts files.`;
    }
    return `This violation impacts the ${context.moduleContext.module} module and must be addressed to maintain Mizan's architectural integrity and business workflow completeness.`;
  }
}

// Main analysis function with Three-Engine Architecture
async function analyzeMizanViolation(violation: MizanViolation): Promise<MizanAnalysisResult> {
  try {
    // Initialize Three-Engine Architecture
    const knowledgeEngine = new KnowledgeEngine();
    const dataEngine = new DataEngine();
    const reasoningEngine = new ReasoningEngine();

    // Knowledge Engine: Get context and best practices
    const architectureContext = await knowledgeEngine.getMizanArchitectureContext();
    const ruleContext = await knowledgeEngine.getRuleContext(violation.rule);

    // Data Engine: Process and normalize violation data
    const processedData = await dataEngine.processViolation(violation);

    // Reasoning Engine: Analyze and generate insights
    const analysisResult = await reasoningEngine.analyzeViolation(
      processedData,
      { architectureContext, ruleContext }
    );

    return analysisResult;
  } catch (error) {
    console.error('Mizan violation analysis failed:', error);
    throw new Error(`Analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

