// Knowledge Engine: Mizan-specific rules and patterns
class MizanKnowledgeEngine {
  private mizanRules: Map<string, any>;
  private architecturePatterns: Map<string, any>;
  private businessWorkflows: Map<string, string[]>;

  constructor() {
    this.mizanRules = new Map();
    this.architecturePatterns = new Map();
    this.businessWorkflows = new Map();
    this.initializeMizanKnowledge();
  }

  private initializeMizanKnowledge(): void {
    // Mizan-specific rules
    this.mizanRules.set('STRICT_TYPESCRIPT_TYPES', {
      pattern: /:\s*any\b|function\s+\w+\s*\([^)]*\)\s*{|async\s+function\s+\w+\s*\([^)]*\)\s*{/,
      severity: 'high',
      description: 'All functions must have explicit TypeScript types',
      mizanContext: 'Critical for Three-Engine Architecture reliability'
    });

    this.mizanRules.set('TENANT_ISOLATION', {
      pattern: /\.select\(\)\.from\(.*\)(?!.*tenantId)/,
      severity: 'critical',
      description: 'Database queries must include tenantId for multi-tenant isolation',
      mizanContext: 'Essential for platform security and data isolation'
    });

    this.mizanRules.set('THREE_ENGINE_PATTERN', {
      pattern: /class\s+\w+Service.*{(?!.*KnowledgeEngine|.*DataEngine|.*ReasoningEngine)/,
      severity: 'high',
      description: 'AI services must implement Three-Engine Architecture',
      mizanContext: 'Core architectural pattern for all AI modules'
    });

    // Business workflows
    this.businessWorkflows.set('culture', ['Culture Survey', 'Recognition Agent', 'Engagement Agent']);
    this.businessWorkflows.set('skills', ['Skills Analysis', 'LXP Triggering', 'Performance Integration']);
    this.businessWorkflows.set('performance', ['Goal Setting', 'Evaluation', 'Calibration']);
  }

  getMizanRule(ruleName: string): any {
    return this.mizanRules.get(ruleName);
  }

  getBusinessWorkflows(module: string): string[] {
    return this.businessWorkflows.get(module) || [];
  }

  getArchitecturePatterns(): Map<string, any> {
    return this.architecturePatterns;
  }
}

// Data Engine: File processing and analysis
class MizanDataEngine {
  private context: MizanAnalysisContext;

  constructor(context: MizanAnalysisContext) {
    this.context = MizanAnalysisContextSchema.parse(context);
  }

  async processProjectFiles(): Promise<Map<string, string>> {
    const fileContents = new Map<string, string>();

    try {
      const projectRoot = this.context.projectRoot;
      const filesToAnalyze = await this.getMizanFiles(projectRoot);

      for (const filePath of filesToAnalyze) {
        try {
          const content = await fs.readFile(filePath, 'utf-8');
          fileContents.set(filePath, content);
        } catch (error) {
          console.error(`Error reading file ${filePath}:`, error);
        }
      }
    } catch (error) {
      console.error('Error processing project files:', error);
      throw new Error(`Failed to process Mizan project files: ${error}`);
    }

    return fileContents;
  }

  private async getMizanFiles(rootPath: string): Promise<string[]> {
    const mizanFiles: string[] = [];
    const extensions = ['.ts', '.tsx', '.js', '.jsx'];

    const scanDirectory = async (dirPath: string): Promise<void> => {
      try {
        const entries = await fs.readdir(dirPath, { withFileTypes: true });

        for (const entry of entries) {
          const fullPath = path.join(dirPath, entry.name);

          if (entry.isDirectory() && !entry.name.startsWith('.') && entry.name !== 'node_modules') {
            await scanDirectory(fullPath);
          } else if (entry.isFile() && extensions.some(ext => entry.name.endsWith(ext))) {
            mizanFiles.push(fullPath);
          }
        }
      } catch (error) {
        console.error(`Error scanning directory ${dirPath}:`, error);
      }
    };

    await scanDirectory(rootPath);
    return mizanFiles;
  }

  normalizeFilePath(filePath: string): string {
    return path.relative(this.context.projectRoot, filePath);
  }
}

// Reasoning Engine: Analysis and violation detection
class MizanReasoningEngine {
  private knowledgeEngine: MizanKnowledgeEngine;
  private context: MizanAnalysisContext;

  constructor(knowledgeEngine: MizanKnowledgeEngine, context: MizanAnalysisContext) {
    this.knowledgeEngine = knowledgeEngine;
    this.context = context;
  }

  analyzeViolations(fileContents: Map<string, string>): MizanAnalysisResult {
    const violations: MizanViolation[] = [];

    for (const [filePath, content] of fileContents) {
      const fileViolations = this.analyzeFile(filePath, content);
      violations.push(...fileViolations);
    }

    return this.generateAnalysisResult(violations);
  }

  private analyzeFile(filePath: string, content: string): MizanViolation[] {
    const violations: MizanViolation[] = [];
    const lines = content.split('\n');

    lines.forEach((line, index) => {
      const lineNumber = index + 1;

      // Check for TypeScript violations
      if (this.checkTypeScriptViolation(line)) {
        violations.push({
          file: filePath,
          line: lineNumber,
          rule: 'STRICT_TYPESCRIPT_TYPES',
          code: line.trim(),
          description: 'Function missing explicit TypeScript types',
          severity: 'high',
          mizanModule: this.detectMizanModule(filePath),
          tenantImpact: this.assessTenantImpact(filePath, line),
          businessWorkflowImpact: this.assessBusinessWorkflowImpact(filePath, line)
        });
      }

      // Check for tenant isolation violations
      if (this.checkTenantIsolationViolation(line)) {
        violations.push({
          file: filePath,
          line: lineNumber,
          rule: 'TENANT_ISOLATION',
          code: line.trim(),
          description: 'Database query missing tenantId filter',
          severity: 'critical',
          mizanModule: this.detectMizanModule(filePath),
          tenantImpact: true,
          businessWorkflowImpact: ['Multi-tenant data isolation', 'Security compliance']
        });
      }

      // Check for Three-Engine pattern violations
      if (this.checkThreeEngineViolation(line, content)) {
        violations.push({
          file: filePath,
          line: lineNumber,
          rule: 'THREE_ENGINE_PATTERN',
          code: line.trim(),
          description: 'AI service missing Three-Engine Architecture',
          severity: 'high',
          mizanModule: this.detectMizanModule(filePath),
          tenantImpact: false,
          businessWorkflowImpact: ['AI analysis reliability', 'Architecture consistency']
        });
      }
    });

    return violations;
  }

  private checkTypeScriptViolation(line: string): boolean {
    const rule = this.knowledgeEngine.getMizanRule('STRICT_TYPESCRIPT_TYPES');
    return rule?.pattern.test(line) || false;
  }

  private checkTenantIsolationViolation(line: string): boolean {
    const rule = this.knowledgeEngine.getMizanRule('TENANT_ISOLATION');
    return rule?.pattern.test(line) || false;
  }

  private checkThreeEngineViolation(line: string, fullContent: string): boolean {
    if (line.includes('class') && line.includes('Service')) {
      return !fullContent.includes('KnowledgeEngine') || 
             !fullContent.includes('DataEngine') || 
             !fullContent.includes('ReasoningEngine');
    }
    return false;
  }

  private detectMizanModule(filePath: string): MizanViolation['mizanModule'] {
    const pathLower = filePath.toLowerCase();
    if (pathLower.includes('culture')) return 'culture';
    if (pathLower.includes('structure')) return 'structure';
    if (pathLower.includes('skills')) return 'skills';
    if (pathLower.includes('performance')) return 'performance';
    if (pathLower.includes('hiring')) return 'hiring';
    if (pathLower.includes('lxp')) return 'lxp';
    if (pathLower.includes('talent')) return 'talent';
    if (pathLower.includes('bonus')) return 'bonus';
    return undefined;
  }

  private assessTenantImpact(filePath: string, line: string): boolean {
    return line.includes('db.select') || 
           line.includes('database') || 
           filePath.includes('service') ||
           filePath.includes('api');
  }

  private assessBusinessWorkflowImpact(filePath: string, line: string): string[] {
    const module = this.detectMizanModule(filePath);
    if (module) {
      return this.knowledgeEngine.getBusinessWorkflows(module);
    }
    return ['Code quality', 'Platform reliability'];
  }

  private generateAnalysisResult(violations: MizanViolation[]): MizanAnalysisResult {
    const summary = {
      totalViolations: violations.length,
      criticalCount: violations.filter(v => v.severity === 'critical').length,
      highCount: violations.filter(v => v.severity === 'high').length,
      mediumCount: violations.filter(v => v.severity === 'medium').length,
      lowCount: violations.filter(v => v.severity === 'low').length
    };

    const affectedModules = [...new Set(violations.map(v => v.mizanModule).filter(Boolean))];
    const brokenWorkflows = [...new Set(violations.flatMap(v => v.businessWorkflowImpact))];

    return {
      violations,
      summary,
      mizanCompliance: {
        threeEnginePattern: !violations.some(v => v.rule === 'THREE_ENGINE_PATTERN'),
        agentTriggering: !violations.some(v => v.businessWorkflowImpact.includes('Agent triggering')),
        moduleIntegration: !violations.some(v => v.businessWorkflowImpact.includes('Module integration')),
        tenantIsolation: !violations.some(v => v.rule === 'TENANT_ISOLATION'),
        workflowCompleteness: brokenWorkflows.length === 0
      },
      businessImpact: {
        affectedModules: affectedModules as string[],
        brokenWorkflows,
        riskLevel: summary.criticalCount > 0 ? 'critical' : 
                  summary.highCount > 0 ? 'high' : 
                  summary.mediumCount > 0 ? 'medium' : 'low'
      },
      recommendations: this.generateRecommendations(violations)
    };
  }

  private generateRecommendations(violations: MizanViolation[]): string[] {
    const recommendations: string[] = [];

    if (violations.some(v => v.rule === 'STRICT_TYPESCRIPT_TYPES')) {
      recommendations.push('Convert JavaScript files to TypeScript and add explicit type annotations');
    }

    if (violations.some(v => v.rule === 'TENANT_ISOLATION')) {
      recommendations.push('Add tenantId filters to all database queries for multi-tenant isolation');
    }

    if (violations.some(v => v.rule === 'THREE_ENGINE_PATTERN')) {
      recommendations.push('Implement Three-Engine Architecture for all AI service classes');
    }

    return recommendations;
  }
}

