/**
 * Parses Mizan business validation XML content with strict type safety
 * Supports Three-Engine Architecture validation patterns
 * @param {string} xmlContent - XML string content to parse
 * @returns {MizanValidationResult} Parsed and validated business data
 * @throws {MizanValidationError} When XML is malformed or invalid
 */
function parseMizanBusinessValidationXML(xmlContent) {
  // Input validation with strict typing
  if (typeof xmlContent !== 'string' || !xmlContent.trim()) {
    throw new MizanValidationError('XML content must be a non-empty string', 'INVALID_INPUT');
  }

  try {
    // Parse XML with error handling
    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(xmlContent, 'text/xml');

    // Check for parsing errors
    const parseError = xmlDoc.querySelector('parsererror');
    if (parseError) {
      throw new MizanValidationError(`XML parsing failed: ${parseError.textContent}`, 'PARSE_ERROR');
    }

    // Extract and validate Mizan-specific elements
    const validationResult = {
      tenantId: extractAndValidateText(xmlDoc, 'tenantId'),
      moduleType: extractAndValidateText(xmlDoc, 'moduleType'),
      validationType: extractAndValidateText(xmlDoc, 'validationType'),
      engineCompliance: {
        knowledgeEngine: extractBooleanAttribute(xmlDoc, 'knowledgeEngine', 'compliant'),
        dataEngine: extractBooleanAttribute(xmlDoc, 'dataEngine', 'compliant'),
        reasoningEngine: extractBooleanAttribute(xmlDoc, 'reasoningEngine', 'compliant')
      },
      agentTriggers: extractAgentTriggers(xmlDoc),
      moduleIntegrations: extractModuleIntegrations(xmlDoc),
      businessWorkflows: extractBusinessWorkflows(xmlDoc),
      timestamp: new Date().toISOString(),
      isValid: true
    };

    // Validate required Mizan fields
    validateMizanRequiredFields(validationResult);

    // Validate Three-Engine Architecture compliance
    validateThreeEngineCompliance(validationResult);

    // Validate tenant isolation
    validateTenantIsolation(validationResult);

    return validationResult;

  } catch (error) {
    if (error instanceof MizanValidationError) {
      throw error;
    }
    throw new MizanValidationError(`Unexpected error parsing XML: ${error.message}`, 'PROCESSING_ERROR');
  }
}

/**
 * Custom error class for Mizan validation errors
 */
class MizanValidationError extends Error {
  constructor(message, code) {
    super(message);
    this.name = 'MizanValidationError';
    this.code = code;
    this.timestamp = new Date().toISOString();
  }
}

/**
 * Extracts and validates text content from XML element
 * @param {Document} xmlDoc - Parsed XML document
 * @param {string} elementName - Element name to extract
 * @returns {string} Validated text content
 */
function extractAndValidateText(xmlDoc, elementName) {
  const element = xmlDoc.querySelector(elementName);
  if (!element || !element.textContent?.trim()) {
    throw new MizanValidationError(`Required element '${elementName}' is missing or empty`, 'MISSING_ELEMENT');
  }
  return element.textContent.trim();
}

/**
 * Extracts boolean attribute from XML element
 * @param {Document} xmlDoc - Parsed XML document
 * @param {string} elementName - Element name
 * @param {string} attributeName - Attribute name
 * @returns {boolean} Boolean value
 */
function extractBooleanAttribute(xmlDoc, elementName, attributeName) {
  const element = xmlDoc.querySelector(elementName);
  if (!element) return false;

  const attrValue = element.getAttribute(attributeName);
  return attrValue === 'true' || attrValue === '1';
}

/**
 * Extracts agent trigger configurations
 * @param {Document} xmlDoc - Parsed XML document
 * @returns {Array} Agent trigger configurations
 */
function extractAgentTriggers(xmlDoc) {
  const triggers = [];
  const triggerElements = xmlDoc.querySelectorAll('agentTrigger');

  triggerElements.forEach(trigger => {
    triggers.push({
      sourceAgent: trigger.getAttribute('source') || '',
      targetAgent: trigger.getAttribute('target') || '',
      triggerType: trigger.getAttribute('type') || '',
      isActive: trigger.getAttribute('active') === 'true'
    });
  });

  return triggers;
}

/**
 * Extracts module integration configurations
 * @param {Document} xmlDoc - Parsed XML document
 * @returns {Array} Module integration configurations
 */
function extractModuleIntegrations(xmlDoc) {
  const integrations = [];
  const integrationElements = xmlDoc.querySelectorAll('moduleIntegration');

  integrationElements.forEach(integration => {
    integrations.push({
      sourceModule: integration.getAttribute('source') || '',
      targetModule: integration.getAttribute('target') || '',
      integrationType: integration.getAttribute('type') || '',
      dataFlow: integration.getAttribute('dataFlow') || '',
      isEnabled: integration.getAttribute('enabled') === 'true'
    });
  });

  return integrations;
}

/**
 * Extracts business workflow configurations
 * @param {Document} xmlDoc - Parsed XML document
 * @returns {Array} Business workflow configurations
 */
function extractBusinessWorkflows(xmlDoc) {
  const workflows = [];
  const workflowElements = xmlDoc.querySelectorAll('businessWorkflow');

  workflowElements.forEach(workflow => {
    workflows.push({
      workflowName: workflow.getAttribute('name') || '',
      workflowType: workflow.getAttribute('type') || '',
      dependencies: workflow.getAttribute('dependencies')?.split(',') || [],
      isComplete: workflow.getAttribute('complete') === 'true',
      priority: workflow.getAttribute('priority') || 'medium'
    });
  });

  return workflows;
}

/**
 * Validates required Mizan fields are present and valid
 * @param {Object} validationResult - Parsed validation result
 */
function validateMizanRequiredFields(validationResult) {
  const requiredFields = ['tenantId', 'moduleType', 'validationType'];

  for (const field of requiredFields) {
    if (!validationResult[field]) {
      throw new MizanValidationError(`Required field '${field}' is missing or invalid`, 'VALIDATION_FAILED');
    }
  }

  // Validate tenantId format (UUID)
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  if (!uuidRegex.test(validationResult.tenantId)) {
    throw new MizanValidationError('tenantId must be a valid UUID format', 'INVALID_TENANT_ID');
  }
}

/**
 * Validates Three-Engine Architecture compliance
 * @param {Object} validationResult - Parsed validation result
 */
function validateThreeEngineCompliance(validationResult) {
  const { engineCompliance } = validationResult;

  if (!engineCompliance.knowledgeEngine || !engineCompliance.dataEngine || !engineCompliance.reasoningEngine) {
    throw new MizanValidationError('All three engines (Knowledge, Data, Reasoning) must be compliant', 'ENGINE_COMPLIANCE_FAILED');
  }
}

/**
 * Validates tenant isolation requirements
 * @param {Object} validationResult - Parsed validation result
 */
function validateTenantIsolation(validationResult) {
  // Ensure all workflows and integrations respect tenant boundaries
  const { businessWorkflows, moduleIntegrations } = validationResult;

  // Check that tenant isolation is maintained in workflows
  const crossTenantWorkflows = businessWorkflows.filter(workflow => 
    workflow.dependencies.some(dep => dep.includes('cross-tenant'))
  );

  if (crossTenantWorkflows.length > 0) {
    throw new MizanValidationError('Cross-tenant workflows detected - tenant isolation violated', 'TENANT_ISOLATION_VIOLATION');
  }
}

/**
 * Extracts XML content for Mizan agent validation with strict typing
 * Ensures production-ready parsing for multi-agent system integrity
 * @param {string} xml - Valid XML string to parse
 * @param {string} tag - XML tag name to extract content from
 * @returns {string | null} - Extracted content or null if not found
 * @throws {Error} - If xml is invalid or tag is empty
 */
function extractXMLContent(xml, tag) {
  // Mizan Production-Ready Validation: Strict input validation
  if (typeof xml !== 'string' || xml.trim() === '') {
    throw new Error(`Mizan Validator Error: Invalid XML input - expected non-empty string, got ${typeof xml}`);
  }

  if (typeof tag !== 'string' || tag.trim() === '') {
    throw new Error(`Mizan Validator Error: Invalid tag input - expected non-empty string, got ${typeof tag}`);
  }

  // Sanitize tag name for security (prevent XML injection)
  const sanitizedTag = tag.trim().replace(/[<>]/g, '');

  try {
    // Production-ready XML parsing with error handling
    const openTag = `<${sanitizedTag}>`;
    const closeTag = `</${sanitizedTag}>`;

    const startIndex = xml.indexOf(openTag);
    if (startIndex === -1) {
      return null; // Tag not found
    }

    const contentStart = startIndex + openTag.length;
    const endIndex = xml.indexOf(closeTag, contentStart);

    if (endIndex === -1) {
      throw new Error(`Mizan Validator Error: Malformed XML - missing closing tag for '${sanitizedTag}'`);
    }

    const content = xml.substring(contentStart, endIndex).trim();

    // Return null for empty content to maintain consistency
    return content === '' ? null : content;

  } catch (error) {
    // Enhanced error handling for Mizan agent reliability
    throw new Error(`Mizan Validator XML Parsing Error: ${error.message}`);
  }

/**
 * Mizan Business Validation Engine
 * Validates business logic integrity across all Mizan modules
 * Ensures Three-Engine Architecture compliance and agent triggering workflows
 */
async function runMizanBusinessValidation(): Promise<MizanValidationResult> {
